document.addEventListener("DOMContentLoaded",()=>{Array.prototype.slice.call(document.querySelectorAll(".navbar-burger"),0).forEach(t=>{t.addEventListener("click",()=>{var e=t.dataset.target,e=document.getElementById(e);t.classList.toggle("is-active"),e.classList.toggle("is-active")})}),document.querySelectorAll(".navbar-link").forEach(function(e){e.addEventListener("click",function(){e.nextElementSibling.classList.toggle("is-hidden-mobile")})}),document.querySelectorAll(".navbar-dropdown").forEach(function(e){e.classList.add("is-hidden-mobile")}),window.addEventListener("scroll",function(){var e=document.getElementById("main_nav");50<window.scrollY?e.classList.add("nav__scrolled"):e.classList.remove("nav__scrolled")})});